import { IItemsAmount } from "../redux/types";
import { IItems } from "../redux/types";
import { CountExpences } from "./CountExpences";

export const InitialiseBasket = () => {
  const data = localStorage.getItem("basket");
  const items = data ? JSON.parse(data) : [];
  const expences = CountExpences(items);

  return {
    items: items as IItems[],
    itemsAmount: items as IItemsAmount[],
    expences,
  };
};
