      <div
        onClick={() =>
          setImageIndex(imageIndex + 1 !== 3 ? imageIndex + 1 : imageIndex)
        }
        className="item__body_image_changeForward"
      >
        <button className="item__body_image_changeForward_button">
          {" "}
          {">"}{" "}
        </button>
      </div>
      <div className="item__body_subcontent">
        <h1>{props.name}</h1>
