import React, { useContext, useState } from "react";
import { Context } from "../../Pages/HomeContext";
import { SetCategory } from "../../redux/home/homeSlice";
import { useAppDispatch, useAppSelector } from "../../redux/hooks";

import '../../styles/style.css';
export const CategoriesSideNav = () => {
    const [sideNavWidth, setSideNavWidth] = useState(5);
    
    const [isOpened, setOpened] = useState(false);
    const [{setCatalogWidth}] = useContext(Context);
  const CATEGORIES = useAppSelector((state) => state.home.categories);
  const dispatch = useAppDispatch();

  const SET_CATEGORY = (category: string) => {
    dispatch(SetCategory(category));
  };
  
  const NAV_SET = () => {
    setOpened(!isOpened);
    if(isOpened){
        setSideNavWidth(0);
        setCatalogWidth(1500);
    }
    else{
        setSideNavWidth(300);
        setCatalogWidth(1200);
    }
    
  }
 

  return (
    <>
      <div id="mySidenav" className="sidenav" style={{width: `${sideNavWidth}px`}}>
        <a className="closebtn" onClick={()=>NAV_SET()}>{"<"}</a>
        {CATEGORIES.map((category) => (
          <h1
            className="categories__items_item"
            key={category.id}
            onClick={() => SET_CATEGORY(category.name)}
          >
            {category.name}
          </h1>
        ))}
      </div>
        <div className="categories__sideNav_open_bkg">
        <span className="categories__sideNav_open" onClick={()=>NAV_SET()}>{">"}</span>
        </div>
    </>
  );
};
export default CategoriesSideNav;
